import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import axios from 'axios';
import { addEditUsers } from "../actions/userActions";
import { TextField, Button, Grid } from "@material-ui/core";
// import Autocomplete from '@material-ui/lab/Autocomplete';

class AddEditUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      namaLengkap: "",
      kewarganegaraan: "",
      posisi: "",
      alamat: "",
      kecamatan: "",
      kota: "",
      provinsi: "",
      open: false,
      list: [],
      listProv: [],
    };
  }

  componentDidMount() {
    const res = axios.get("https://dev.farizdotid.com/api/daerahindonesia/provinsi");
    this.setState({ listProv: res });
    console.log(ListProv);
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = () => {
    const user = {
      namaLengkap: this.state.namaLengkap,
      kewarganegaraan: this.state.kewarganegaraan,
      posisi: this.state.posisi,
      alamat: this.state.alamat,
    };
    const res = axios.post("https://reqaid.com/api/FakePosts", user);
    this.setState({ list: res });
  };

  render() {
    const { namaLengkap, kewarganegaraan, posisi, alamat } = this.state;
    return (
      <Grid>
        <TextField
          label="Nama Lengkap" // Label acts like placeholder
          onChange={this.handleChange}
          value={namaLengkap}
          fullWidth
          name="namaLengkap"
          margin="normal"
          variant="outlined"
          required
        />

        <TextField
          label="Kewarganegaraan" // Label acts like placeholder
          onChange={this.handleChange}
          value={kewarganegaraan}
          fullWidth
          name="kewarganegaraan"
          margin="normal"
          variant="outlined"
          required
        />

        <TextField
          label="Posisi" // Label acts like placeholder
          onChange={this.handleChange}
          value={posisi}
          fullWidth
          name="posisi"
          margin="normal"
          variant="outlined"
          required
        />

        <TextField
          label="Alamat" // Label acts like placeholder
          onChange={this.handleChange}
          value={alamat}
          fullWidth
          name="alamat"
          margin="normal"
          variant="outlined"
        />

        <Autocomplete
          freeSolo
          disableClearable
          options={fetchProvinsi.map((option) => option.namaLengkap)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Provinsi"
              margin="normal"
              variant="outlined"
              InputProps={{ ...params.InputProps, type: 'search' }}
            />
          )}
        />

        <Button
          type="submit"
          variant="contained"
          onClick={this.handleSubmit}
          color="primary"
          style={{ margin: "1em", float: "right" }}
        >
          Simpan
        </Button>
      </Grid>
    );
  }
}

AddEditUser.propTypes = {
  addNewUser: PropTypes.func,
  snackBarMessage: PropTypes.string,
  snackBarVariant: PropTypes.string,
};

const mapStateToProps = (state) => ({
  snackBarMessage: state.utils.message,
  snackBarVariant: state.utils.variant,
});

export default connect(mapStateToProps, { addNewUser: addEditUsers })(
  AddEditUser
);
