import React, { Component } from 'react';

import { Formik } from 'formik';
import * as Yup from 'yup';

import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { addEditUsers } from '../actions/userActions';
import { TextField, Button, Grid } from '@material-ui/core';

import SnackBar from './SnackBar';

class AddEditUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {
        id: '',
        namaLengkap: '',
        kewarganegaraan: '',
        posisi: '',
        alamat: '',
        kecamatan: '',
        kota: '',
        provinsi: '',
      },
      open: false
    };
    this.handleClose = this.handleClose.bind(this);
    this.handleOpen = this.handleOpen.bind(this);
  }

  handleOpen() {
    this.setState({ open: true });
  }

  handleClose(event, reason){
    if (reason === "clickaway") {
      return;
    }
    this.setState({open: false});
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    })
  }

  render() {
    return (
      <Grid>
        <TextField
          name="namaLengkap"
          label="Nama Lengkap" // Label acts like placeholder
          onChange={this.handleChange}
          // onBlur={handleBlur}
          // value={values.name}
          fullWidth
          margin="normal"
          variant="outlined"
          required
        />

        <TextField
          name="kewarganegaraan"
          label="Kewarganegaraan" // Label acts like placeholder
          onChange={this.handleChange}
          // onBlur={handleBlur}
          // value={values.email}
          fullWidth
          margin="normal"
          variant="outlined"
          required
        />

        <TextField
          name="phone"
          label="Phone Number" // Label acts like placeholder
          onChange={this.handleChange}
          // onBlur={handleBlur}
          // value={values.phone}
          fullWidth
          margin="normal"
          variant="outlined"
          required
        />

        <TextField
          name="website"
          label="Website" // Label acts like placeholder
          onChange={this.handleChange}
          // onBlur={handleBlur}
          // value={values.website}
          fullWidth
          margin="normal"
          variant="outlined"
        />

        <Button
          // disabled={!dirty && !isSubmitting}
          type="submit"
          variant="contained"
          color="primary"
          style={{ margin: '1em', float: 'right' }}
        >
          {this.state.user.edit ? 'Update User' : 'Add User'}
        </Button>
      </Grid>
    )
  }
}

AddEditUser.propTypes = {
  addNewUser: PropTypes.func,
  snackBarMessage: PropTypes.string,
  snackBarVariant: PropTypes.string
};

const mapStateToProps = state => ({
  snackBarMessage: state.utils.message,
  snackBarVariant: state.utils.variant
})

export default connect(
  mapStateToProps,
  { addNewUser: addEditUsers }
)(AddEditUser);
